def encode(self, value, value_max, index, length):

    value = min(value, value_max)
    multiplier = self.nb_of_symbols ** length - 1
    encoded_value = int(multiplier * value / value_max)
    encoded_array = []

    for i in range(length):
        power = self.nb_of_symbols ** (length - i - 1)
        symbol = encoded_value // power
        encoded_value -= symbol * power

        encoded_array.append(symbol)
        self.write(symbol, index + i)

    return encoded_array

